document.addEventListener("DOMContentLoaded",(function(){initContactForm();initFaqAccordion()}));function initContactForm(){const contactForm=document.getElementById("contact-form");const formStatus=document.getElementById("form-status");if(!contactForm||!formStatus)return;contactForm.addEventListener("submit",(function(e){e.preventDefault();const formData={name:contactForm.querySelector("#name").value,email:contactForm.querySelector("#email").value,subject:contactForm.querySelector("#subject").value,message:contactForm.querySelector("#message").value};if(!formData.name||!formData.email||!formData.message){showFormStatus("error","Please fill in all required fields.");return}if(!isValidEmail(formData.email)){showFormStatus("error","Please enter a valid email address.");return}showFormStatus("success","Thank you for your message! I will get back to you soon.");contactForm.reset()}));const formInputs=contactForm.querySelectorAll("input, textarea");formInputs.forEach((input=>{input.addEventListener("blur",(function(){validateInput(this)}));input.addEventListener("input",(function(){this.classList.remove("error")}))}));function validateInput(input){if(input.required&&!input.value.trim()){input.classList.add("error");return false}if(input.id==="email"&&!isValidEmail(input.value)){input.classList.add("error");return false}input.classList.remove("error");return true}function isValidEmail(email){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return emailRegex.test(email)}function showFormStatus(type,message){formStatus.textContent=message;formStatus.className="form-status "+type;setTimeout((()=>{formStatus.className="form-status"}),5e3)}}function initFaqAccordion(){const faqQuestions=document.querySelectorAll(".faq-question");if(!faqQuestions.length)return;faqQuestions.forEach((question=>{question.addEventListener("click",(function(){this.classList.toggle("active");faqQuestions.forEach((q=>{if(q!==this&&q.classList.contains("active")){q.classList.remove("active")}}))}))}))}function initSocialLinkTracking(){const socialLinks=document.querySelectorAll('.footer-social a, .info-card a[href^="https://www.linkedin.com"], .info-card a[href^="https://github.com"]');socialLinks.forEach((link=>{link.addEventListener("click",(function(e){const platform=this.getAttribute("aria-label")||"Social";console.log(`${platform} link clicked`)}))}))}function initCopyToClipboard(){const contactInfoItems=document.querySelectorAll(".info-card");contactInfoItems.forEach((item=>{const infoValue=item.querySelector(".info-content p");if(!infoValue)return;item.addEventListener("click",(function(){const textToCopy=infoValue.textContent;navigator.clipboard.writeText(textToCopy).then((()=>{const notification=document.createElement("div");notification.className="copy-notification";notification.textContent="Copied to clipboard!";this.appendChild(notification);setTimeout((()=>{notification.remove()}),2e3)})).catch((err=>{console.error("Failed to copy text: ",err)}))}))}))}