document.addEventListener("DOMContentLoaded",(function(){initTimelineAnimation()}));function initTimelineAnimation(){const timelineItems=document.querySelectorAll(".timeline-item");const observer=new IntersectionObserver((entries=>{entries.forEach((entry=>{if(entry.isIntersecting){entry.target.classList.add("animated");observer.unobserve(entry.target)}}))}),{threshold:.1});timelineItems.forEach((item=>{observer.observe(item)}))}function initInteractiveTimeline(){const timelineItems=document.querySelectorAll(".timeline-item");timelineItems.forEach((item=>{item.addEventListener("click",(function(){this.classList.toggle("expanded");const jobDescription=this.querySelector(".job-description");if(this.classList.contains("expanded")){jobDescription.style.maxHeight=jobDescription.scrollHeight+"px"}else{jobDescription.style.maxHeight=null}}))}))}function initAchievementAnimation(){const achievementCards=document.querySelectorAll(".achievement-card");const observer=new IntersectionObserver((entries=>{entries.forEach((entry=>{if(entry.isIntersecting){const delay=Array.from(achievementCards).indexOf(entry.target)*.2;entry.target.style.animationDelay=delay+"s";entry.target.classList.add("fade-in");observer.unobserve(entry.target)}}))}),{threshold:.1});achievementCards.forEach((card=>{observer.observe(card)}))}function initResumeDownloadTracking(){const downloadButton=document.querySelector(".download-button .btn");if(!downloadButton)return;downloadButton.addEventListener("click",(function(){console.log("Resume downloaded")}))}