document.addEventListener("DOMContentLoaded",(function(){initSkillBars()}));function initSkillBars(){const skillBars=document.querySelectorAll(".skill-progress-bar");const observer=new IntersectionObserver((entries=>{entries.forEach((entry=>{if(entry.isIntersecting){const width=entry.target.style.width;entry.target.style.width="0";setTimeout((()=>{entry.target.style.width=width}),100);observer.unobserve(entry.target)}}))}),{threshold:.1});skillBars.forEach((bar=>{observer.observe(bar)}))}function initSkillFiltering(){const filterButtons=document.querySelectorAll(".skill-filter-btn");const skillCategories=document.querySelectorAll(".skill-category");if(!filterButtons.length||!skillCategories.length)return;filterButtons.forEach((button=>{button.addEventListener("click",(function(){filterButtons.forEach((btn=>btn.classList.remove("active")));this.classList.add("active");const filter=this.getAttribute("data-filter");skillCategories.forEach((category=>{if(filter==="all"||category.getAttribute("data-category")===filter){category.style.display="block"}else{category.style.display="none"}}))}))}))}function initSkillsRadarChart(){}